options(error=traceback)
library(testwhat)
.solution <- "${solutionEscaped}"

.solutionfile <- "${solutionFileEscaped}"


setup_state(sol_code = .solutionfile, stu_code = .solution)

tryCatch({
    ${test}
    cat(paste("\033[32m", tw$get()$success_msg, "\033[0m", sep = ""))
}, error = function(e) {
    cat(paste("\033[31m", e[1], "\033[0m", sep = ""))
})

node.code:
test <- function() {
    # Here we can either check objects created in the solution code, or the
    # string value of the solution, available as .solution. See the testTemplate
    # in the meta.json for details.
    if (!grepl("head(mtcars)", .solution, fixed = TRUE)) {
        stop("Are you printing the correct variable?")
    }

    # This function is defined in the testTemplate
    success("Well done!") # nolint
}
